"use strict";(self.webpackChunkvns=self.webpackChunkvns||[]).push([[270],{53315:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>c,contentTitle:()=>a,default:()=>l,frontMatter:()=>o,metadata:()=>i,toc:()=>d});const i=JSON.parse('{"id":"SOVA/technical-frameworks/grundlagen","title":"Overview of SOME/IP","description":"Service-Oriented Middleware over IP (SOME/IP) ist eine spezielle Technologie, die in der Automobilindustrie zur","source":"@site/docs/SOVA/04_technical-frameworks/01_grundlagen.md","sourceDirName":"SOVA/04_technical-frameworks","slug":"/SOVA/technical-frameworks/grundlagen","permalink":"/Vehicle-Network-Standards/docs/SOVA/technical-frameworks/grundlagen","draft":false,"unlisted":false,"editUrl":"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/docs/SOVA/04_technical-frameworks/01_grundlagen.md","tags":[],"version":"current","sidebarPosition":1,"frontMatter":{},"sidebar":"sovaSidebar","previous":{"title":"Technical Frameworks and Protocols","permalink":"/Vehicle-Network-Standards/docs/category/technical-frameworks-and-protocols"},"next":{"title":"Technical Frameworks and Protocols","permalink":"/Vehicle-Network-Standards/docs/SOVA/technical-frameworks/00"}}');var t=r(74848),s=r(28453);const o={},a="Overview of SOME/IP",c={},d=[];function u(e){const n={h1:"h1",header:"header",p:"p",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.header,{children:(0,t.jsx)(n.h1,{id:"overview-of-someip",children:"Overview of SOME/IP"})}),"\n",(0,t.jsx)(n.p,{children:'Service-Oriented Middleware over IP (SOME/IP) ist eine spezielle Technologie, die in der Automobilindustrie zur\nRealisierung der Service-orientierten Kommunikation innerhalb von Fahrzeugen verwendet wird. Dieses Protokoll\nerm\xf6glicht den Datenaustausch zwischen Service-Providern (Server) und anfragenden Clients im Kontext des\nSystems Engineering im Automobilsektor. Die Entwicklung und Standardisierung von SOME/IP wurde ma\xdfgeblich\nvom AUTOSAR-Konsortium vorangetrieben und wird kontinuierlich von verschiedenen Unternehmen\nweiterentwickelt.SOME/IP dient als Standardprotokoll f\xfcr den Datenaustausch \xfcber Service-Schnittstellen innerhalb eines Fahrzeugs\nund fungiert als Middleware f\xfcr Service-orientierte Architekturen (SOA), um den Datenaustausch zwischen Servern  und Clients zu erleichtern. Es setzt sich aus drei grundlegenden Technologiebl\xf6cken zusammen: "Service\nDiscovery" (Diensterkennung), "Serialisierung" (Datenformatierung) und "Transport\nProtocols" (Transportprotokolle).\nDie "Service Discovery" erm\xf6glicht es, verf\xfcgbare Dienste im Netzwerk zu identifizieren und auf sie zuzugreifen. Die\n"Serialisierung" ist f\xfcr die Umwandlung von Daten in ein geeignetes Format verantwortlich, um sie zwischen den\nKommunikationspartnern austauschbar zu machen. Schlie\xdflich k\xfcmmert sich das "Transport Protocols" um die\neigentliche \xdcbertragung der Daten zwischen den Servern und den Clients.\nInsgesamt tr\xe4gt SOME/IP dazu bei, die Kommunikation und den Datenaustausch innerhalb von Fahrzeugen\neffizienter und standardisiert zu gestalten. Diese Technologie wird weiterhin in der Automobilbranche entwickelt\nund verbessert, um den steigenden Anforderungen an die Vernetzung und den Informationsaustausch innerhalb\nmoderner Fahrzeuge gerecht zu werden.'})]})}function l(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(u,{...e})}):u(e)}},28453:(e,n,r)=>{r.d(n,{R:()=>o,x:()=>a});var i=r(96540);const t={},s=i.createContext(t);function o(e){const n=i.useContext(s);return i.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:o(e.components),i.createElement(s.Provider,{value:n},e.children)}}}]);